    textoArray.forEach(function(letra, i){   

      setTimeout(function(){
          elemento.innerHTML += letra;
        }, 75 * i)
      });*/
      console.log(obj)
  }
  const titulo = document.getElementById("titulo-principal");

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>
          <strong function={setInterval(mudarCor, 1000)} id="color" className="red">Blog </strong>
          do Paulo Spiguel
        </h1>
        <p function={typeWrite(titulo)} id="titulo-principal">Em breve estarei publicado novidades. Fique ligado ...</p>
      </header>
    </div>
  );
}

export default App;
